    public boolean hasGroupsSizeX(int[] deck) {
        int len = deck.length;
        if (len<=1){
            return false;
        }
        Map<Integer, Integer> map = new HashMap<>();
        for (int i = 0; i < len; i++) {
            int num;
            if (map.containsKey(deck[i])) {
                num=map.get(deck[i])+1;
            } else {
                num=1;
            }
            map.put(deck[i], num);
        }
        int min=Integer.MAX_VALUE;
        for (Integer i:map.values()){
            min=Math.min(min, i);
        }
        for (int i = 2; i <= min; i++) {
            boolean b=true;
            for (Integer j:map.values()){
                if(j%i!=0) {
                    b=false;
                    break;
                }
            }
            if (b){
                return true;
            }
        }
        return false;
    }

